import React, { useState } from 'react';
const Eventdata = () =>{
    // array destructing
    const purplecolor ="purple";
    const [bg , Setbg]= useState(purplecolor);
    const [name , setName] = useState('Click Me');
    const [count , setCount] = useState(0); 
    const [Name , Setname] = useState('clcik Me')
    const IncNum = () =>{
        setCount(count + 1);
        Setname("Click More");
    };
    const bgChange = () =>{
        const newbg = '#34495e';
        Setbg (newbg);
        setName ("Ouch"); 
    }
    const bgName = () =>{
        Setbg(purplecolor);
        setName("Click Me")
    }
    return(
        <React.Fragment>
            <div className="input-data" style={ {backgroundColor: bg} }>
                <button onMouseEnter= {bgChange} onMouseLeave={bgName}>{name}</button>
            </div>
            <div className="visiterClick">
                <h1>{count}</h1>
                <button onClick={IncNum}>{Name}</button>
            </div>
        </React.Fragment>
    );
};

export default Eventdata;
