import React from 'react';

const SlotM = (props) =>{
    // let x ='Smile';
    // let y ='Smile';
    // let z ='Smile';
    let x =props.x;
    let y =props.y;
    let z =props.z;

    if(x===y && y===z){
        return(
            <React.Fragment>
                <div className="slot__wrapper">
                    <p><span>{x}</span><span>{y}</span><span>{z}</span></p>
                    <h2>This is Matching</h2>
                    <hr />
                </div>
            </React.Fragment>
        )
    }
    else{
        return(
            <React.Fragment>
                <div className="slot__wrapper">
                    <p><span>{x}</span><span>{y}</span><span>{z}</span></p>
                    <h2>This is not Matching</h2>
                    <hr />
                </div>
            </React.Fragment>
        )
    }
}
const App = () =>{
    return(
       <React.Fragment>
           <h1 className="heading"> 
                Slot Welcome to <span style={{fontWeight:'bold'}}>Slot machine Game</span> 
           </h1>
           <div className="slot__innerwraper">
                <SlotM x='Smile' y='Smile' z='Smile' />
                <SlotM x='Santa' y='Manta' z='banta'/>
                <SlotM x='singh' y='singh' z='singh'/>
           </div>
       </React.Fragment>
    );
};
export default SlotMaching;
