import React, { useState } from 'react';
const Handlingmultiform = () =>{
    const [FirstName , SetFirstName] = useState("");
    const [LastName , SetLastName] = useState("");
    const [EmailId , SetEmailId] = useState("");
    const [FullData , SetFullData] = useState("");

    const FirstNameEvent = (event) =>{
        console.log(event.target.value);
        SetFirstName(event.target.value);
    }
    const LastNameEvent =(event) =>{
        SetLastName(event.target.value);
    }
    const EmailInputEvent =(event) =>{
        SetEmailId(event.target.value);
    }
    const FormSubmit = (event)=>{
        event.preventDefault();
        SetFullData(FirstName , LastName , EmailId);
    }

    return(
        <React.Fragment>
            <form onSubmit ={FormSubmit}>
                <div className="form-data">
                    <h2>Hello {FullData}</h2>
                    <input type='text' value={FirstName} placeholder='Enter Your Name' onChange ={FirstNameEvent} />
                    <br />
                    <input type='text' value={LastName} placeholder='Enter Your Last Name' onChange ={LastNameEvent} />
                    <br />
                    <input type='text' value={EmailId} placeholder='Enter Your Email' onChange ={EmailInputEvent} />
                    <button type="submit">Submit <span role="img" aria-label="Link">ðŸ”—</span></button>
                </div>
            </form>
        </React.Fragment>

    );

}

export default Handlingmultiform;
